SELECT h.hitcount, s.name AS leverancier, c.name AS stad, m.name AS gemeente, d.code AS provincie FROM mhl_hitcount h JOIN mhl_suppliers s ON h.supplier_ID = s.id JOIN mhl_cities c ON s.city_ID = c.id JOIN mhl_communes m ON c.commune_ID = m.id JOIN mhl_districts d ON m.district_ID = d.id WHERE h.year = 2014 AND h.month = 1 AND d.code IN ('LB', 'NB', 'ZL') ORDER BY h.hitcount DESC;
